import cv2import numpy as npfrom matplotlib import pyplot as pltimage = cv2.imread('../data/opencv_logo.png', 0)image2 = image.copy()template = image[100:200, 100:200]w, h = template.shape# All the 6 methods for comparison in a listmethods = ['cv2.TM_CCOEFF', 'cv2.TM_CCOEFF_NORMED', 'cv2.TM_CCORR',           'cv2.TM_CCORR_NORMED', 'cv2.TM_SQDIFF', 'cv2.TM_SQDIFF_NORMED']for method in methods:    image = image2.copy()    # eval 语句用来计算存储在字符串中的有效 Python 表达式    method = eval(method)    # 使用模版匹配    res = cv2.matchTemplate(image, template, method)    min_val, max_val, min_loc, max_loc = cv2.minMaxLoc(res)    # 使用不同的比较方法，对结果的解释不同    # If the method is TM_SQDIFF or TM_SQDIFF_NORMED, take minimum    if method in [cv2.TM_SQDIFF, cv2.TM_SQDIFF_NORMED]:        top_left = min_loc    else:        top_left = max_loc    bottom_right = (top_left[0] + w, top_left[1] + h)    cv2.rectangle(image, top_left, bottom_right, 255, 2)    plt.subplot(121), plt.imshow(res, cmap='gray')    plt.title('Matching Result'), plt.xticks([]), plt.yticks([])    plt.subplot(122), plt.imshow(image, cmap='gray')    plt.title('Detected Point'), plt.xticks([]), plt.yticks([])    plt.suptitle(method)    plt.show()