import  cv2'''扩展缩放'''def resize(image):    '''    功能： 缩放    函数： resise(图片，尺寸，比例，插值方法) # 其中尺寸和比例有一个就可以了    '''    # 这里只设置比例    res = cv2.resize(image, None, fx=2, fy=2, interpolation=cv2.INTER_CUBIC)    # 或者： 这里设置尺寸    height, width = image.shape[:2]    res = cv2.resize(image, (2 * width, 2 * height), interpolation=cv2.INTER_CUBIC)    while True:        cv2.imshow('res', res)        cv2.imshow('image', image)        if cv2.waitKey(1) & 0xFF == 27:            break        pass    cv2.destroyAllWindows()    passif __name__ == '__main__':    image = cv2.imread('../data/plane.jpg')    resize(image)    pass