import cv2import numpy as np'''HSV在 OpenCV 的 HSV 格式中， H（色彩/色度）的取值范围是 [0， 179]，S（饱和度）的取值范围 [0， 255]， V（亮度）的取值范围 [0， 255]。但是不同的软件使用的值可能不同。所以当你需要拿 OpenCV 的 HSV 值与其他软件的 HSV 值进行对比时，一定要记得归一化''''''物体跟踪'''def object_track():    '''    功能： 跟踪一定得蓝色范围    步骤：        1. 从视频中获取每一帧图像        2. 将图像转换到 HSV 空间        3. 设置 HSV 阈值到蓝色范围。        4. 获取蓝色物体，当然我们还可以做其他任何我们想做的事，比如：在蓝色物体周围画一个圈    '''    cap = cv2.VideoCapture(0)    while 1:        ret, frame = cap.read()        hsv = cv2.cvtColor(frame, cv2.COLOR_BGR2HSV)        # 【蓝色的阈值，饱和度， 亮度】        lower_blue = np.array([110, 50, 50])        upper_blue = np.array([130, 255, 255])        # 构建掩码        mask = cv2.inRange(hsv, lower_blue, upper_blue)        # 对原图像和掩码位运算        res = cv2.bitwise_and(frame, frame, mask=mask)        cv2.imshow('frame', frame)        cv2.imshow('mask', mask)        cv2.imshow('res', res)        k = cv2.waitKey(5) & 0xFF        if k == 27:            break        pass    passif __name__ == '__main__':    object_track()    pass